INSERT INTO `tbl_posts` (`post_id`, `post_title`, `post_slug`, `post_views`, `post_desc`, `post_content`, `post_status`, `post_image`, `cate_post_id`) VALUES
(1, 'ASUS ZenFone 8 series được xác nhận có giắc cắm tai nghe 3.5mm', 'asus-zenfone-8-series-duoc-xac-nhan-co-giac-cam-tai-nghe-35mm', 28, '<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi d&ograve;ng ZenFone 7 được c&ocirc;ng bố v&agrave;o năm ngo&aacute;i, hai chiếc điện thoại trong series n&agrave;y đều thiếu giắc cắm &acirc;m thanh 3.5mm. ASUS dường như đ&atilde; nhận ra tầm quan trọng của cổng kết nối n&agrave;y khi teaser mới nhất của họ cho Zenfone 8 đ&atilde; tiết lộ sự trở lại của giắc cắm tai nghe 3.5mm.</span></span></p>', '<p dir=\"ltr\"><span style=\"font-size:20px\">Như bạn c&oacute; thể thấy ở video teaser ph&iacute;a tr&ecirc;n, ASUS&nbsp;x&aacute;c nhận giắc cắm tai nghe 3.5mm sẽ xuất hiện tr&ecirc;n c&aacute;c flagship tiếp theo của m&igrave;nh, đ&oacute; l&agrave; d&ograve;ng ZenFone 8. Tuy nhi&ecirc;n, ch&uacute;ng ta vẫn kh&ocirc;ng chắc liệu cổng kết nối n&agrave;y c&oacute; được trang bị cho cả ba điện thoại&nbsp;ZenFone 8, ZenFone 8 Mini v&agrave; ZenFone 8 Pro hay kh&ocirc;ng.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\">Ngo&agrave;i giắc cắm &acirc;m thanh, Zenfone 8 cũng sẽ c&oacute; một lỗ đục ở g&oacute;c tr&ecirc;n c&ugrave;ng b&ecirc;n tr&aacute;i của m&agrave;n h&igrave;nh để chứa camera trước. Điều n&agrave;y c&oacute; nghĩa l&agrave; camera lật của hai thế hệ Zenfone trước sẽ kh&ocirc;ng trở lại. Điện thoại cũng sẽ c&oacute; bộ vi xử l&yacute; Snapdragon 888, tốc độ l&agrave;m tươi cao v&agrave; m&agrave;n h&igrave;nh OLED với độ ph&acirc;n giải 1080p.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\"><img alt=\"Thông tin ZenFone 8\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134521/Originals/thong-tin-Zenfone-8-2.jpg\" title=\"Thông tin ZenFone 8\" style=\"width:700px;height:500px\"/></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:20px\">Chưa hết, c&oacute; th&ocirc;ng tin cho rằng Zenfone 8 Mini sẽ c&oacute; k&iacute;ch thước cực kỳ nhỏ gọn với m&agrave;n h&igrave;nh 5.92 inch, pin 4,000mAh v&agrave; hỗ trợ sạc nhanh 30W. Điện thoại n&agrave;y c&ograve;n được tiết lộ sẽ c&oacute; tới 5 phi&ecirc;n bản cấu h&igrave;nh khi ra mắt, đ&oacute; l&agrave; 6GB/128GB, 8GB/128GB, 8GB/256GB, 12GB/256GB, 16GB/256GB. ZenFone 8 mini được đồn đại sẽ sử dụng chipset Snapdragon 888&nbsp;mạnh mẽ. Ở mặt sau, n&oacute; được tiết lộ sẽ c&oacute; camera ch&iacute;nh IMX686 64MP v&agrave; cảm biến Sony IMX663 mới.&nbsp;</span></p>', 0, 'thong-tin-zenfone-8-cover18.jpg', 1),
(3, 'Hướng dẫn cách tìm phần cứng bị lỗi trên máy tính bằng công cụ và phần mềm', 'huong-dan-cach-tim-phan-cung-bi-loi-tren-may-tinh-bang-cong-cu-va-phan-mem', 27, '<p><span style=\"font-size:16px\">Bạn muốn kiểm tra lỗi phần cứng m&aacute;y t&iacute;nh nhưng kh&ocirc;ng biết l&agrave;m thế n&agrave;o? Dưới đ&acirc;y l&agrave; c&aacute;c b&agrave;i kiểm tra v&agrave; chuẩn đo&aacute;n phần cứng m&aacute;y t&iacute;nh tốt nhất để t&igrave;m v&agrave; ph&aacute;t hiện lỗi trước khi qu&aacute; muộn.</span></p>', '<p><span style=\"font-size:16px\">Mỗi m&aacute;y t&iacute;nh đều được cấu tạo từ rất nhiều phần cứng v&agrave; mỗi phần cứng đều c&oacute; nguy cơ gặp sự cố. C&aacute;c phần cứng sinh nhiệt hoặc c&aacute;c bộ phận chuyển động c&oacute; xu hướng hỏng trước nhất. Nhờ c&aacute;c b&agrave;i kiểm tra phần cứng m&aacute;y t&iacute;nh, bạn c&oacute; thể qu&eacute;t hệ thống v&agrave; ph&aacute;t hiện phần cứng bị lỗi trước khi mọi thứ gặp sự cố. Dưới đ&acirc;y l&agrave; c&aacute;ch thực hiện kiểm tra phần cứng tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tu-van/lam-the-nao-de-cai-dat-windows-10-tren-may-tinh-32137\">Windows 10</a>.</span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><strong>C&ocirc;ng cụ chẩn đo&aacute;n phần cứng Windows 10</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Windows 10 c&oacute; hai c&ocirc;ng cụ chuẩn đo&aacute;n&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cach-sua-loi-o-cung-thong-dung-128876\">lỗi phần cứng</a>&nbsp;t&iacute;ch hợp sẵn gồm: Performance Monitor v&agrave; Windows Memory Diagnostic. B&acirc;y giờ ch&uacute;ng ta h&atilde;y c&ugrave;ng t&igrave;m hiểu chi tiết về từng c&ocirc;ng cụ.</span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>Performance Monitor</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Đ&acirc;y l&agrave; c&ocirc;ng cụ to&agrave;n diện nhất của hệ điều h&agrave;nh Windows 10 để định vị v&agrave; chuẩn đo&aacute;n c&aacute;c vấn đề về phần cứng v&agrave; hệ thống.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Truy cập menu&nbsp;<strong><em>Start</em></strong>, nhập&nbsp;<strong><em>Performance Monitor</em></strong>&nbsp;v&agrave;o khung&nbsp;<em>Search</em>&nbsp;rồi k&iacute;ch chọn kết quả tương ứng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Công cụ chẩn đoán phần cứng Windows 10 - 1\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/perfmon-line-view.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để c&oacute; c&aacute;i nh&igrave;n tổng quan nhanh về phần cứng của hệ thống, ở khung b&ecirc;n tr&aacute;i h&atilde;y truy cập menu&nbsp;<strong><em>Reports &gt; System &gt; System Diagnostics &gt; [T&ecirc;n m&aacute;y t&iacute;nh]</em></strong>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nếu muốn t&igrave;m hiểu kỹ hơn về phần cứng, bạn c&oacute; thể truy cập biểu đồ hiệu suất trực tiếp từ menu&nbsp;<strong><em>Monitoring Tools &gt; Performance Monitor</em></strong>&nbsp;v&agrave; tập dữ liệu c&oacute; thể t&ugrave;y chỉnh (từ mục&nbsp;<em>Data Collector Sets</em>).</span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>Windows Memory Diagnostic</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Bộ xử l&yacute; trung t&acirc;m của m&aacute;y t&iacute;nh (CPU) sử dụng RAM để lưu trữ th&ocirc;ng tin ngắn hạn. Mọi thứ c&oacute; trong RAM sẽ bị mất khi tắt m&aacute;y. Trong trường hợp bạn gặp c&aacute;c lỗi hệ thống như hiệu năng giảm, m&aacute;y t&iacute;nh thường xuy&ecirc;n bị treo, card đồ họa kh&ocirc;ng tải được khi khởi động, tệp dữ liệu bị hỏng hay th&ocirc;ng tin RAM kh&ocirc;ng ch&iacute;nh x&aacute;c &hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&oacute; thể nguy&ecirc;n nh&acirc;n do bộ nhớ RAM bị lỗi.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bạn c&oacute; thể sử dụng c&ocirc;ng cụ Windows Memory Diagnostic để kiểm tra lỗi bộ nhớ RAM nếu c&oacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Công cụ chẩn đoán phần cứng Windows 10 - 2\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/restart-check-memory-problems-tool.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để khởi chạy c&ocirc;ng cụ, nhấn tổ hợp ph&iacute;m&nbsp;<strong><em>Windows + R</em></strong>, trong hộp thoại Run nhập&nbsp;<strong><em>mdsched.exe</em></strong>&nbsp;v&agrave; nhấn&nbsp;<strong><em>Enter</em></strong>. M&aacute;y t&iacute;nh sẽ khởi động lại.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Windows Memory Diagnostic sẽ kiểm tra hệ thống, khi kết th&uacute;c m&aacute;y sẽ khởi động lại một lần nữa. Bạn sẽ thấy kết quả của m&agrave;n h&igrave;nh sau khi quay lại m&agrave;n h&igrave;nh Windows.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Công cụ chẩn đoán phần cứng Windows 10 - 3\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/windows-memory-diagnostic-scan.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nếu Windows kh&ocirc;ng tự động hiển thị kết quả, h&atilde;y mở&nbsp;<em>Event Viewer&nbsp;</em>bằng c&aacute;ch k&iacute;ch chuột phải v&agrave;o menu&nbsp;<strong><em>Start &gt; Windows Logs &gt; System</em></strong>&nbsp;v&agrave; t&igrave;m tệp gần đ&acirc;y nhất c&oacute; t&ecirc;n&nbsp;<strong><em>MemoryDiagnostic</em></strong>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Công cụ chẩn đoán phần cứng Windows 10 - 4\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/event-viewer.jpg\" /></span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><strong>Ứng dụng chẩn đo&aacute;n phần cứng của b&ecirc;n thứ ba</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Nếu đang t&igrave;m kiếm một c&ocirc;ng cụ chuẩn đo&aacute;n hệ thống mạnh hơn, bạn cần sử dụng phần mềm của b&ecirc;n thứ ba. Dưới đ&acirc;y l&agrave; những phần mềm chuẩn đo&aacute;n hệ thống miễn ph&iacute; tốt nhất.</span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>MemTest86</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">MemTest86 l&agrave; c&ocirc;ng cụ tốt nhất để kiểm tra RAM tr&ecirc;n Windows. Ứng dụng c&oacute; 13 thuật to&aacute;n kiểm tra RAM kh&aacute;c nhau v&agrave; hỗ trợ RAM DDR4, DDR2 v&agrave; DDR3.&nbsp;Bạn c&oacute; thể khởi chạy MemTest86 trực tiếp từ USB, đĩa CD, thẻ nhớ v&agrave; quan trọng l&agrave; ứng dụng tương th&iacute;ch với chế độ Khởi động an to&agrave;n (Secure Boot).</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Ứng dụng chẩn đoán phần cứng của bên thứ ba - 1\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/memtest_mainmenu.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Kh&ocirc;ng giống như c&ocirc;ng cụ Windows, MemTest86 cũng c&oacute; giao diện đồ họa đầy đủ t&iacute;nh năng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Download&nbsp;<a href=\"https://www.memtest86.com/\" rel=\"nofollow\">MemTest86</a>&nbsp;</strong></span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>CrystalDiskInfo</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Nếu muốn kiểm tra ổ cứng th&igrave; CrystalDiskInfo l&agrave; lựa chọn. Ứng dụng sử dụng c&ocirc;ng nghệ SMART (Self-Monitoring, Analysis, and Reporting Technology) để cung cấp dữ liệu về ổ đĩa bao gồm tỷ lệ đọc lỗi, số lượng c&aacute;c khu vực được ph&acirc;n bổ lại, thời gian quay v&ograve;ng &hellip;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Ứng dụng chẩn đoán phần cứng của bên thứ ba - 2\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/CrystalDiskInfo8_10_en.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">CrystalDiskInfo c&ograve;n bao gồm c&ocirc;ng cụ quản l&yacute; năng lượng v&agrave; quản l&yacute; &acirc;m thanh ti&ecirc;n tiến. Nếu để ứng dụng chạy ẩn, ứng dụng c&oacute; thể đưa ra c&aacute;c cảnh b&aacute;o trực tiếp nếu ổ đĩa trở qu&aacute; n&oacute;ng hoặc đang gặp phải c&aacute;c lỗi kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Download&nbsp;<a href=\"https://crystalmark.info/en/software/crystaldiskinfo/\" rel=\"nofollow\">CrystalDiskInfo</a></strong></span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>HWiNFO</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">HWiNFO l&agrave; một trong những ứng dụng kiểm tra chẩn đo&aacute;n m&aacute;y t&iacute;nh tốt nhất hiện c&oacute;. Ứng dụng cung cấp c&aacute;c b&aacute;o c&aacute;o v&agrave; biểu đồ theo thời gian thực chi tiết nhất trong số c&aacute;c ứng dụng c&oacute; t&iacute;nh năng tương tự về CPU, GPU, bo mạch chủ, ổ đĩa v&agrave; thiết bị ngoại vi của m&aacute;y t&iacute;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Ứng dụng chẩn đoán phần cứng của bên thứ ba - 3\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/heinfo.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bạn cũng c&oacute; thể tải xuống một loạt c&aacute;c add-in cung cấp cho HWiNFO c&aacute;c chức năng bổ sung bao gồm: Bộ chỉnh m&agrave;n h&igrave;nh, tiện &iacute;ch v&agrave; tr&igrave;nh xem nhật k&yacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Download&nbsp;<a href=\"https://www.hwinfo.com/\" rel=\"nofollow\">HWiNFO</a></strong>&nbsp;</span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>RWEverything</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Bạn c&oacute; thể sử dụng RWEverything để kiểm tra hầu hết mọi kh&iacute;a cạnh của mọi phần cứng trong m&aacute;y. Quan trọng hơn, ứng dụng cho ph&eacute;p người d&ugrave;ng điều chỉnh v&agrave; &aacute;p dụng bất kỳ thay đổi c&agrave;i đặt n&agrave;o li&ecirc;n quan đến phần cứng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Ứng dụng chẩn đoán phần cứng của bên thứ ba - 4\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134433/Originals/RWEverything-to-extrieve-windows-8-key-from-BIOS1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tuy nhi&ecirc;n RWEverything kh&ocirc;ng ph&ugrave; hợp cho người d&ugrave;ng kh&ocirc;ng hiểu r&otilde; về phần cứng. Nếu kh&ocirc;ng c&oacute; thể l&agrave;m hỏng phần cứng kh&ocirc;ng thể sửa chữa được.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Download&nbsp;<a href=\"http://rweverything.com/\" rel=\"nofollow\">RWEverything</a>&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tr&ecirc;n đ&acirc;y, FPTShop vừa hướng dẫn c&aacute;c bạn những c&aacute;ch kh&aacute;c nhau để kiểm tra &quot;sức khỏe&quot; phần cứng. Việc kiểm tra phần cứng gi&uacute;p người d&ugrave;ng sớm x&aacute;c định được những nguy&ecirc;n nh&acirc;n tiềm ẩn của lỗi phần cứng nếu c&oacute;.</span></p>', 0, 'kiem-tra-loi-phan-cung3.jpg', 1),
(4, 'Vivo X60t Pro đạt chứng nhận 3C với sạc nhanh 33W', 'vivo-x60t-pro-dat-chung-nhan-3c-voi-sac-nhanh-33w', 19, '<p><span style=\"font-size:16px\">D&ograve;ng X60 của Vivo hiện bao gồm 4 điện thoại th&ocirc;ng minh - Vivo X60, X60 Pro, X60 Pro+ v&agrave; X60t. Tuy nhi&ecirc;n, c&ocirc;ng ty Trung Quốc vẫn chưa muốn dừng lại khi theo một r&ograve; rỉ mới nhất biến thể Vivo X60t Pro đ&atilde; được ph&aacute;t hiện tr&ecirc;n danh s&aacute;ch chứng nhận 3C.</span></p>', '<p dir=\"ltr\"><span style=\"font-size:16px\">Theo 3C, X60t Pro sẽ đi k&egrave;m với số model l&agrave; V2120A. Tại đ&acirc;y, smartphone n&agrave;y được x&aacute;c nhận sẽ hỗ trợ sạc 33W v&agrave; mạng 5G - giống như X60t. Cơ quan chứng nhận Trung Quốc kh&ocirc;ng tiết lộ bất cứ điều g&igrave; kh&aacute;c về X60t Pro, nhưng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;th&ocirc;ng minh dự kiến ​​sẽ được cung cấp sức mạnh xử l&yacute; đến từ con chip&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/mediatek-ra-mat-bo-doi-soc-dimensity-1200-va-1100-130864\" title=\"Dimensity 1200\" type=\"Dimensity 1200\">Dimensity 1200</a>.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">Ngo&agrave;i ra, ch&uacute;ng ta c&oacute; thể hy vọng Vivo X60t Pro sẽ đi k&egrave;m với một số cải tiến so với X60t, được cung cấp sức mạnh bởi Dimensity SoC 1100, chạy OriginOS dựa tr&ecirc;n hệ điều h&agrave;nh Android 11 ngay khi xuất xưởng v&agrave; được cung cấp trong một cấu h&igrave;nh bộ nhớ duy nhất - RAM 8GB v&agrave; nhớ trong 128GB.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">Ở mặt trước, Vivo X60t được trang bị m&agrave;n h&igrave;nh&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-amoled-la-gi-34866\" title=\"màn hình AMOLED\" type=\"màn hình AMOLED\">m&agrave;n h&igrave;nh AMOLED</a>&nbsp;6.56 inch, hỗ trợ độ ph&acirc;n giải Full-HD+ v&agrave; tần số qu&eacute;t 120Hz. M&agrave;n h&igrave;nh điện thoại n&agrave;y c&ograve;n được t&iacute;ch hợp cảm biến v&acirc;n tay b&ecirc;n dưới v&agrave; được cung cấp năng lượng từ vi&ecirc;n pin 4,300 mAh.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">Về khả năng nhiếp ảnh, Vivo X60t c&oacute; tổng cộng bốn camera sau, bao gồm cảm biến ch&iacute;nh 48MP v&agrave; m&aacute;y ảnh selfie 32MP chất lượng. Hy vọng trong v&agrave;i ng&agrave;y tới, ch&uacute;ng ta sẽ được nh&igrave;n thấy nhiều th&ocirc;ng tin hơn về Vivo X60t xuất hiện tr&ecirc;n c&aacute;c trang mạng.</span></p>', 0, 'thong-tin-zenfone-8-cover10.jpg', 1),
(5, 'Apple phát hành bản beta thứ hai của tvOS 14.6 cho các nhà phát triển', 'apple-phat-hanh-ban-beta-thu-hai-cua-tvos-146-cho-cac-nha-phat-trien', 16, '<p><span style=\"font-size:16px\">C&ugrave;ng với c&aacute;c bản cập nhật iOS 14.6, iPadOS 14.6 v&agrave; watchOS 7.5 beta 2 th&igrave; Apple mới đ&acirc;y cũng vừa ph&aacute;t h&agrave;nh bản beta thứ hai của hệ điều h&agrave;nh tvOS 14.6 cho c&aacute;c nh&agrave; ph&aacute;t triển.</span></p>', '<p dir=\"ltr\"><span style=\"font-size:16px\">Được biết, bản cập nhật mới d&agrave;nh cho&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/apple-watch\" title=\"Apple Watch\" type=\"Apple Watch\">Apple Watch</a>&nbsp;dường như chỉ tập trung v&agrave;o việc sửa c&aacute;c lỗi c&ograve;n tồn đọng m&agrave; kh&ocirc;ng mang đến bất kỳ t&iacute;nh năng n&agrave;o mới.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Apple phát hành bản beta thứ hai của tvOS 14.6 cho các nhà phát triển\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134512/Originals/tvOS-14-6-beta-2.jpg\" title=\"Apple phát hành bản beta thứ hai của tvOS 14.6 cho các nhà phát triển\" /></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">Theo trang Macrumors, bản cập nhật tvOS 14.6 beta 2 d&agrave;nh cho nh&agrave; ph&aacute;t triển hiện đang được tung ra cho người d&ugrave;ng sử dụng Apple TV thế hệ thứ tư hoặc thứ năm. Bản cập nhật n&agrave;y hiện c&oacute; thể được tải xuống Apple TV th&ocirc;ng qua cấu h&igrave;nh được c&agrave;i đặt bằng Xcode.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">Mặc d&ugrave; vậy, đừng mong đợi bất kỳ thay đổi lớn n&agrave;o trong bản cập nhật n&agrave;y. Đầu tuần n&agrave;y,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\" title=\"Apple\" type=\"Apple\">Apple</a>&nbsp;cũng vừa ph&aacute;t h&agrave;nh bản cập nhật tvOS 14.5 gi&uacute;p mang đến một v&agrave;i t&iacute;nh năng mới cho Apple TV, bao gồm hỗ trợ tay cầm chơi&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/giai-tri\">game</a>&nbsp;DualSense của PlayStation 5 v&agrave; Xbox Series X mới nhất, để người d&ugrave;ng c&oacute; thể sử dụng ch&uacute;ng để chơi game từ tvOS.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">tvOS 14.5 cũng mang đến t&iacute;nh năng Điều chỉnh c&acirc;n bằng m&agrave;u sắc cho tất cả c&aacute;c Apple TV hiện c&oacute;. Với set-top-box v&agrave; c&aacute;c cảm biến ti&ecirc;n tiến của iPhone, Apple TV sử dụng cảm biến &aacute;nh s&aacute;ng trong iPhone để so s&aacute;nh c&acirc;n bằng m&agrave;u sắc với c&aacute;c th&ocirc;ng số kỹ thuật ti&ecirc;u chuẩn của ng&agrave;nh m&agrave; c&aacute;c nh&agrave; quay phim tr&ecirc;n to&agrave;n thế giới sử dụng.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:16px\">Bản cập nhật tvOS 14.5 mới cũng hỗ trợ th&ecirc;m cho Apple TV 4K thế hệ thứ hai v&agrave; Siri Remote mới. Dự kiến, c&aacute;c Apple TV 4K thế hệ thứ hai v&agrave; Siri Remote mới sẽ c&oacute; sẵn để đặt h&agrave;ng v&agrave;o thứ 6 tuần n&agrave;y, ng&agrave;y 30/4.</span></p>', 0, 'vos-14-6-beta-2-cover36.jpg', 1),
(6, 'FPT Shop tặng gói bảo hành mở rộng 2 năm cho smartphone OPPO', 'fpt-shop-tang-goi-bao-hanh-mo-rong-2-nam-cho-smartphone-oppo', 27, '<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Từ ng&agrave;y 01 - 31/05/2021, FPT Shop tặng th&ecirc;m g&oacute;i bảo h&agrave;nh mở rộng 2 năm cho tất cả smartphone OPPO c&oacute; gi&aacute; từ 4 triệu trở l&ecirc;n, &aacute;p dụng c&ugrave;ng l&uacute;c với những ưu đ&atilde;i hiện c&oacute; của sản phẩm.</span></span></p>', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:16px\">Như vậy, kh&aacute;ch h&agrave;ng chọn mua những chiếc samrtphone được y&ecirc;u th&iacute;ch nhất của OPPO như: A54, A73, A92, A94, Reno4, Reno5... tại FPT Shop đều được tặng th&ecirc;m g&oacute;i bảo h&agrave;nh mở rộng 2 năm,&nbsp;d&agrave;i hơn đến gấp 2 lần so với thời gian th&ocirc;ng thường. Đ&acirc;y ch&iacute;nh l&agrave; lời cam kết về chế độ hậu m&atilde;i c&ugrave;ng chất lượng sản phẩm tốt nhất m&agrave; FPT Shop d&agrave;nh tặng c&aacute;c Ofan tại Việt Nam.</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/134496/Originals/OPPO-Reno5-%20(6).jpg\" style=\"width:700px;height:533px\"/></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:16px\"><em>Từ ng&agrave;y 01 - 31/05, FPT Shop tặng g&oacute;i bảo h&agrave;nh mở rộng 2 năm cho tất cả smartphone OPPO c&oacute; gi&aacute; từ 4 triệu trở l&ecirc;n.</em></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:16px\">Kh&ocirc;ng chỉ tặng th&ecirc;m g&oacute;i bảo h&agrave;nh chất lượng, FPT Shop c&ograve;n mạnh tay giảm đến 1.800.000 đồng cho nhiều smartphone OPPO trong chương tr&igrave;nh &quot;Thống nhất l&agrave; sale tất&quot; diễn ra